import React , { Component }  from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
     isTimerShow: false,
     time:0
    };

    this.runTimer = this.runTimer.bind(this);
    this.hideTimer = this.hideTimer.bind(this);
    this.updateTime = this.updateTime.bind(this);

  }

  runTimer() {
    this.setState((prevState, props) => {
      return { time: 0};
    });
    setInterval(()=>this.updateTime(),1000);
  }

  updateTime(){
    this.setState((prevState, props) => {
      return { time: (prevState.time)+1};
    });
  }

  hideTimer() {
    this.setState((prevState, props) => {
      return { isTimerShow: (!prevState.isTimerShow)};
    });

  }

  render() {
    return (
      <div className="App">
        <h1>
          Hello How are you?
        </h1>
         <button onClick={this.runTimer}>
          Run Timer
        </button>
        <br></br>
        <br></br>
        <label>Show Elapsed Time</label><input type="checkbox" onClick={this.hideTimer} />
        <div>
          <br></br>
          {
            this.state.isTimerShow
            ? 'Elapse Time : ' + this.state.time
            :''
          }
        </div>
      </div>
    );
  }
}


export default App;
