import React , { Component }  from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
     isTimerShow: false,
     time:0
    };

    this.runTimer = this.runTimer.bind(this);
    this.HideTimer = this.HideTimer.bind(this);

  }

  runTimer() {
    this.runTimer=setInterval(updateTime(),1);
  }

  updateTime(){
    this.setState((prevState, props) => {
      return { time: (prevState.time)++};
    });
  }

  HideTimer() {
    this.setState((prevState, props) => {
      return { isTimerShow: (!prevState.isTimerShow)};
    });

  }

  render() {
    return (
      <div className="App">
        <h1>
          Hello How are you?
        </h1>
         <button onClick={this.runTimer}>
          Run Timer
        </button>
        <br></br>
        <br></br>
        <label>Show Elapsed Time</label><input type="checkbox" onClick={this.HideTimer} />
      <div>
      <br></br>
        Elapsed Time : {this.state.time}
      </div>
      </div>
    );
  }
}


export default App;
