import React , { Component }  from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
     isTimerShow: true
    };

    this.runTimer = this.runTimer.bind(this);
    this.HideTimer = this.HideTimer.bind(this);

  }

  runTimer(event) {
    alert("Run Clicked");
  }

  HideTimer(event) {
    this.setState((prevState, props) => {
      return { isTimerShow: (!prevState.isTimerShow)};
    });

  }

  render() {
    return (
      <div className="App">
        <h1>
          Hello How are you?
        </h1>
         <button onClick={this.runTimer}>
          Run Timer
        </button>
        <br></br>
        <br></br>
        <label>Show Elapsed Time</label><input type="checkbox" checked="true" onClick={this.HideTimer} />
      </div>
    );
  }
}


export default App;
